const workflowStaticData = $getWorkflowStaticData('global');

if (!workflowStaticData.videoResults) {
    workflowStaticData.videoResults = {
        videos: [],
        searchTerm: '',
        timestamp: new Date().toISOString()
    };
}

// Get current video data
const currentVideo = $input.item;

// Set search term if not already set
if (!workflowStaticData.videoResults.searchTerm && $node["YouTube Search"].parameter.filters?.q) {
    workflowStaticData.videoResults.searchTerm = $node["YouTube Search"].parameter.filters.q;
}

// Add current video to results
workflowStaticData.videoResults.videos.push(currentVideo);

// Continue batch processing
return currentVideo;

// Get accumulated results from workflow static data
const workflowStaticData = $getWorkflowStaticData('global');
const results = workflowStaticData.videoResults || { videos: [], searchTerm: '', timestamp: new Date().toISOString() };

// Format final response
let responseText = '';

if (results.videos.length === 0) {
    responseText = 'No qualifying videos found for the search term.';
} else {
    responseText = results.videos.map(video => {
        return JSON.stringify({
            id: video.id,
            title: video.title,
            channelTitle: video.channelTitle,
            channelId: video.channelId,
            viewCount: video.viewCount,
            likeCount: video.likeCount,
            commentCount: video.commentCount,
            engagementRate: video.engagementRate,
            description: video.description,
            tags: video.tags,
            publishedAt: video.publishedAt
        });
    }).join(' ### NEXT VIDEO FOUND: ### ');
}

// Clear static data for next search
workflowStaticData.videoResults = { videos: [], searchTerm: '', timestamp: '' };

return [{ 
    response: responseText,
    videoCount: results.videos.length,
    searchTerm: results.searchTerm
}];

