// Initialize or get existing data from workflow static data
const workflowStaticData = $getWorkflowStaticData('global');

if (!workflowStaticData.tiktokResults) {
    workflowStaticData.tiktokResults = {
        videos: [],
        searchTerm: '',
        timestamp: new Date().toISOString(),
        trendingHashtags: new Set(),
        trendingMusic: new Set()
    };
}

// Get current video data
const currentVideo = $input.item;

// Extract and track trending hashtags
if (currentVideo.hashtags) {
    const hashtags = currentVideo.hashtags.split(', ').filter(tag => tag.trim());
    hashtags.forEach(tag => workflowStaticData.tiktokResults.trendingHashtags.add(tag));
}

// Track trending music
if (currentVideo.musicTitle && currentVideo.musicTitle !== 'No music') {
    workflowStaticData.tiktokResults.trendingMusic.add(currentVideo.musicTitle);
}

// Add current video to results
workflowStaticData.tiktokResults.videos.push(currentVideo);

return currentVideo;

// Get accumulated results from workflow static data
const workflowStaticData = $getWorkflowStaticData('global');
const results = workflowStaticData.tiktokResults || { 
    videos: [], 
    searchTerm: '', 
    timestamp: new Date().toISOString(),
    trendingHashtags: new Set(),
    trendingMusic: new Set()
};

// Sort by engagement rate and total engagement (combined score)
results.videos.sort((a, b) => {
    const scoreA = (a.engagementRate * 0.6) + (Math.log10(a.totalEngagement + 1) * 0.4);
    const scoreB = (b.engagementRate * 0.6) + (Math.log10(b.totalEngagement + 1) * 0.4);
    return scoreB - scoreA;
});

// Take top 8 videos
const topVideos = results.videos.slice(0, 8);

// Convert Sets to Arrays for JSON serialization
const trendingHashtags = Array.from(results.trendingHashtags).slice(0, 15);
const trendingMusic = Array.from(results.trendingMusic).slice(0, 10);

// Calculate trend insights
const insights = {
    avgEngagementRate: topVideos.length > 0 ? 
        Math.round((topVideos.reduce((sum, v) => sum + v.engagementRate, 0) / topVideos.length) * 100) / 100 : 0,
    totalViews: topVideos.reduce((sum, v) => sum + v.playCount, 0),
    avgDuration: topVideos.length > 0 ? 
        Math.round(topVideos.reduce((sum, v) => sum + v.duration, 0) / topVideos.length) : 0,
    topHashtags: trendingHashtags,
    trendingMusic: trendingMusic
};

// Format final response
let responseText = '';

if (topVideos.length === 0) {
    responseText = 'No qualifying TikTok videos found for the search term. This could be due to: 1) Very specific search terms, 2) API rate limits, 3) Limited recent viral content in this niche, or 4) Geographic restrictions.';
} else {
    // Add trend insights at the beginning
    responseText = `TIKTOK_TREND_INSIGHTS: ${JSON.stringify(insights)} ### `;

    // Add individual videos
    responseText += topVideos.map(video => {
        return JSON.stringify({
            id: video.id,
            description: video.description,
            authorName: video.authorName,
            authorUsername: video.authorUsername,
            playCount: video.playCount,
            likeCount: video.likeCount,
            commentCount: video.commentCount,
            shareCount: video.shareCount,
            totalEngagement: video.totalEngagement,
            engagementRate: video.engagementRate,
            hashtags: video.hashtags,
            musicTitle: video.musicTitle,
            musicAuthor: video.musicAuthor,
            duration: video.duration,
            createTime: video.createTime,
            videoUrl: video.videoUrl,
            platform: video.platform
        });
    }).join(' ### NEXT TIKTOK VIDEO: ### ');
}

// Clear static data for next search
workflowStaticData.tiktokResults = { 
    videos: [], 
    searchTerm: '', 
    timestamp: '',
    trendingHashtags: new Set(),
    trendingMusic: new Set()
};

return [{ 
    response: responseText,
    videoCount: topVideos.length,
    searchTerm: results.searchTerm,
    platform: 'TikTok',
    insights: insights
}];
